<script lang="ts">
    import * as btc from "@scure/btc-signer";
    import { base64, hex } from "@scure/base";
    import chalk from "chalk";

    export let meta;

    const txstr = ``;

    const tx = btc.RawTx.decode(hex.decode(txstr));

    function decompile(witness: Uint8Array): btc.ScriptType | null {
        try {
            return btc.Script.decode(witness);
        } catch (e) {
            return null;
        }

        return null;
    }

    for (const witnesses of tx.witnesses ?? [[]]) {
        for (const witness of witnesses) {
            const res = decompile(witness);
            if (res) {
                for (const line of res) {
                    if (line instanceof Uint8Array) {
                        console.log(
                            chalk.blue.bold("line is a Uint8Array:"),
                            hex.encode(line)
                        );
                    } else if (Object.values(btc.OP).includes(line)) {
                        console.log(
                            chalk.yellow.bold("line is OP:"),
                            chalk.green(line)
                        );
                    } else if (typeof line === "number") {
                        console.log("line is number:", line);
                    } else {
                        console.log("line is not a Uint8Array:", line);
                    }
                }
            }
        }
    }

    $: highlightedJson = JSON.stringify(meta, null, 4);
</script>

{#if meta}
    <div
        class="flex flex-col text-info leading-relaxed p-4 bg-white border-t bottom-0"
    >
        <div class="mt-2 font-light leading-relaxed break-all">
            <pre><code class="json">{highlightedJson}</code></pre>
        </div>
    </div>
{/if}
